import React,{Component,useState} from 'react';
import { Image, StyleSheet, Text, TouchableOpacity, View,TextInput  } from 'react-native';
import logo from './assets/logo.png';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';




export default function SignUp ({ navigation: { navigate } }) {

   const [email, setEmail] = useState('');
   const [password, setpassword] = useState('');
   const [name, setname] = useState('');
   const [surname, setsurname] = useState('');
   const [phone, setphone] = useState('');
   const [taxnumber, settaxnumber] = useState('');

   return(
    <View style={styles.container}>
      <Image source={logo} style={styles.logo} />
	  <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Email"
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={email => setEmail(email)}/>

      <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Password"
		secureTextEntry={true}
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={val => this.onChangeText('password', val)}/>

	  <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Name"
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={val => this.onChangeText('name', val)}/>

	  <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Surname"
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={val => this.onChangeText('surname', val)}/>

	  <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Phone"
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={val => this.onChangeText('phone', val)}/>

	  <TextInput style = {styles.input}
        underlineColorAndroid = "transparent"
        placeholder = "Tax Number"
        placeholderTextColor = "#9a73ef"
        autoCapitalize = "none"
		onChangeText={val => this.onChangeText('taxnumber', val)}/>


      <TouchableOpacity onPress={() => navigate('Succes')} style={styles.button}>
        <Text style={styles.buttonText}>Register</Text>
      </TouchableOpacity>
    </View>
 );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
	padding:10,

  },
  logo: {
	flex:5,
    alignSelf: 'stretch',
    width: undefined,
    height: undefined,
	resizeMode: 'contain',
  },
  instructions: {
    color: '#888',
    fontSize: 18,
    marginHorizontal: 15,
    marginBottom: 10,
  },
  button: {
	flex:1,
	alignSelf: 'center',
	justifyContent: 'center',
    backgroundColor: "#9400D3",
    padding: 10,
    borderRadius: 5,

  },
  buttonText: {

    fontSize: 20,
    color: '#fff',
  }, 
   input: {
	  flex:1,
	  margin:10,
	  padding:5,
      height: 40,
      borderColor: '#7a42f4',
      borderWidth: 1,
	  borderRadius: 5,


   },

});
